mapper.select:
select email, name, joinDate, role, introduce, profileImg, count(distinct post.postNo) as postCnt, count(distinct commentNo) as commentCnt
from member left join post using(email)
left join comment using(email)
group by email;

select role
from member
where email = #{email}

select email
from member
where email = #{email}

select name
from member
where name = #{name}

mapper.update:
update member
set	introduce = #{introduce}
where email=#{email}

update member
set pwd = #{pwd}
where email=#{email}

update member
set profileImg = #{profileImg}
where email=#{email}

update member
set profileImg = null
where email=#{email}

mapper.select:
select email, pwd, name, role
from member
where email=#{email}

select pwd
from member
where email=#{email}
